import React, { useState } from "react";

function Counter() {
  const [counter, setCounter] = useState(0);
  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h1>Counter App</h1>
      <p>Counter : {counter}</p>
    </div>
  );
}

export default Counter;
