import React, { useState } from "react";

function Counter() {
  const [counter, setCounter] = useState(0);
  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h1>Counter App</h1>
      <p>Counter : {counter}</p>
      <button
        style={{
          margin: "0 5px",
          padding: "5px",
          color: "white",
          backgroundColor: "green",
        }}
      >
        Increament++
      </button>
      <button
        style={{
          margin: "0 5px",
          padding: "5px",
          color: "white",
          backgroundColor: "red",
        }}
      >
        Decreament
      </button>
      <button
        style={{
          margin: "0 5px",
          padding: "5px",
          color: "white",
          backgroundColor: "black",
        }}
      >
        Reset
      </button>
    </div>
  );
}

export default Counter;
