import React, { useState, createContext, useContext } from "react";
//  create an instance of the context to hold the state of the count function
const CountContext = createContext();
function counterProvider({ children }) {
  const [count, setCount] = useState(0);
  // function to increase the count
  const increament = () => {
    setCount(count + 1);
  };
  // function to decrease the count
  const decreament = () => {
    setCount(count - 1);
  };
  return (
    <CountContext.Provider value={{ count, increament, decreament }}>
      {children}
    </CountContext.Provider>
  );
}
function App() {
  return (
    <div>
      <h1>Counter : 0</h1>
      <button onClick={() => console.log("increased")}>Increament</button>
      <button onClick={() => console.log("decreased")}>Decreament</button>
    </div>
  );
}

export default App;
